import React, { useState } from 'react';
import { PatientCollection } from '../../api/Collections/PatientCollection';


 const PatientForm = () => {
  const [name,firstLastName, setName, setFirstLastName] = useState("");

  const handleSubmit = e => {
    e.preventDefault();

    if (!name) return;

    PatientCollection.insert({
      name: name.trim(),
      firstLastName: firstLastName.trim(),
      createdAt: new Date()
    });

    setText("");
  };

  return (
    <form className="task-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Type to Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      /><br></br>
      <input
        type="text"
        placeholder="Type firstLastName"
        value={firstLastName}
        onChange={(e) => setFirstLastName(e.target.value)}
      /><br></br>

      <button type="submit" value={handleSubmit}>Add Task</button>
    </form>
  );
};

export default PatientForm;


