import React, { useState } from 'react';
import { PatientCollection } from '../../api/Collections/PatientCollection';
import { useForm } from "react-hook-form";

 const PatientForm = () => {

 //SetState and Const for parameters
  const [name, setName] = useState("");
  const [FirstLastName,setFirstLastName ] = useState("");
  const [SecondLastName,setSecondLastName ] = useState("");
  const [Rut,setRut ] = useState("");
  const [zipCode,setZipCode ] = useState("");
  const [state,setState ] = useState("");
  const [county,setCounty ] = useState("");




  const {handleSubmit , register} = e => {

    e.preventDefault();

    if (!name) return;

    PatientCollection.insert({
     name, FirstLastName, SecondLastName, Rut, zipCode, state, county,
    });

    setName("");
    setFirstLastName("")
    setSecondLastName("")
    setRut("")
    setZipCode("")
    setState("")
    setCounty("")
  };

  return (
    <form className="task-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Type to Name"
        value={name}
        onChange={(e) => setName(e.target.value)}

      />
      <input
        type="text"
        placeholder="Type firstLastName"
        value={FirstLastName}
        onChange={(e) => setFirstLastName(e.target.value)}
      />
       <input
        type="text"
        placeholder="Type Second Last Name"
        value={SecondLastName}
        onChange={(e) => setSecondLastName(e.target.value)}
      />
       <input
        type="text"
        placeholder="Type to rut"
        value={Rut}
        onChange={(e) => setRut(e.target.value)}
      />
       <input
        type="text"
        placeholder="Type to zip code"
        value={zipCode}
        onChange={(e) => setZipCode(e.target.value)}
      />
       <input
        type="text"
        placeholder="Select Your State"
        value={state}
        onChange={(e) => setState(e.target.value)}
      />
       <input
        type="text"
        placerholder="Select Your County"
        value={county}
        onChange={(e) => setCounty(e.target.value)}
      />

      <button type="submit" value={handleSubmit}>Add Task</button>
    </form>
  );
};

export default PatientForm;

